function combiner(V) {
    if(!V.length) return [];

    var R = V[V.length-1];

    function F(V, length) {
        if(!length) return;

        var S = R; R = [];

        V[length-1].forEach(v => S.forEach(s => R.push(v+s)));
        F(V, length-1);
    }

    F(V, V.length-1);
    return R;
}

module.exports = combiner;
