var onlyOne = require('./onlyOne.js');

function concat(V, length) {
    var Vall = new Array(length);

    for(var n=0; n<length; n++) {
        Vall[n] = [];
        for(var l in V) {
            Vall[n] = Vall[n].concat(V[l][n]);
        }
        Vall[n] = onlyOne(Vall[n]);
    }

    return Vall;
}

module.exports = concat;
