var idn      = require('./idn.js');
var combiner = require('./combiner.js');

function zv(IN) {
    var TB = idn(IN, ["cn_zh", "tw_zh"]);
    var ZV = {}
    if(TB) {
        for(var l in TB.PV) {
            var s = combiner(TB.PV[l]);
            if(s.length) ZV[l] = s[0];
        }
    }
    return ZV;
}

module.exports = zv;
